import './App.css';
import {useState} from 'react'
import WeekDay from './WeekDay';



function App() {
  const [rate, setRate] = useState([(Math.random() *5).toFixed(0), (Math.random() *5).toFixed(0), (Math.random() *5).toFixed(0), (Math.random() *5).toFixed(0), (Math.random() *5).toFixed(0),(Math.random() *5).toFixed(0),(Math.random() *5).toFixed(0), (Math.random() *5).toFixed(1)]);
  console.log(rate)
  let weekDay = new Date()
  weekDay = weekDay.getDay();
  const [dayName, setDay] = useState(["일", "월", "화", "수", "목", "금", "토"])
  return (
    <div className="App">
    <div className="container" style={{
        border:"solid black",
        height: "90vh",
        width: "50vw",
        margin: "auto",
        marginTop: "20px",
        padding: "10px",
        borderRadius: "5px",
      }}>
      <h2>내 일주일 평가</h2>
      <WeekDay/>
      <div className="day_list" style={{
        height: "60%",
        width:"90%",
        border:"solid #ddd",
        margin: "auto",
      }}>
      <WeekDay/>
      </div>
      <div className="averageText">평균 평점
        <div className="averageNum">{rate}</div>
      </div>
      <button onClick={()=>setRate(0)}>Reset</button>
      </div> 
    </div>
  );
}

export default App;
