import './App.css';
import {useState} from 'react'
import WeekDay from './WeekDay';



function App() {
  const randArr = Array.from({length:7}).map(()=>(Math.random()*5).toFixed(0))
  const [rate, setRate] = useState(randArr);
  let sum = 0;
  let aveRate = 0;
  for (let i = 0; i<rate.length; i++){
    sum += parseInt(rate[i])
  }
  aveRate = (sum/rate.length).toFixed(1)
  const [dayName, setDay] = useState(["일", "월", "화", "수", "목", "금", "토"])
  return (
    <div className="App">
    <div className="container" style={{
        border:"solid black",
        height: "90vh",
        width: "50vw",
        margin: "auto",
        marginTop: "20px",
        padding: "10px",
        borderRadius: "5px",
      }}>
      <h2>내 일주일 평가</h2>

      <div className="day_list" style={{
        height: "60%",
        width:"90%",
        border:"solid #ddd",
        margin: "auto",
      }}>
      <WeekDay rate={rate} dayName={dayName}/>
      <WeekDay />
      </div>
      <div className="averageText">평균 평점
        <div className="averageNum">{aveRate}</div>
      </div>
      <button onClick={()=>setRate("0")}>Reset</button>
      </div> 
    </div>
  );
}

export default App;
